record.field:
for record in records: lot_number = record.name if (len(lot_number) < 5) or (not record.use_expiration_date) or (record.product_id.default_code[0] != 'E'): pass
else: year, day = "20"+lot_number[:2], int(lot_number[2:]) year_date = datetime.datetime.fromisoformat(year+"-01-01") create_date = year_date + datetime.timedelta(days=(day)) expiration_date = create_date + datetime.timedelta(days=record.product_id[0].expiration_time) best_before_date = expiration_date - datetime.timedelta(days=record.product_id[0].use_time) removal_date = expiration_date - datetime.timedelta(days=record.product_id[0].removal_time+1) alert_date = expiration_date - datetime.timedelta(days=record.product_id[0].alert_time+1) record.write({ "expiration_date": expiration_date.strftime("%Y-%m-%d %H:%M:%S"), "use_date": best_before_date.strftime("%Y-%m-%d %H:%M:%S"), "removal_date": removal_date.strftime("%Y-%m-%d %H:%M:%S"), "alert_date": alert_date.strftime("%Y-%m-%d %H:%M:%S")
})
